import "./comment-list.css";
import swal from "sweetalert";
import { useState } from "react";
import UpdateCommentModal from "./UpdateCommentModal";

const CommentList = () => {
  const [updateComment, setUpdateComment] = useState(false);
  const [commentForUpdate, setCommentForUpdate] = useState(null);

  const deletePostHandler = () => {
    swal({
      title: "Are you sure?",
      text: "Once deleted, you will not be able to recover this imaginary file!",
      icon: "warning",
      buttons: true,
      dangerMode: true,
    }).then((willDelete) => {
      if (willDelete) {
        swal("Poof! Your imaginary file has been deleted!", {
          icon: "success",
        });
      } else {
        swal("Your imaginary file is safe!");
      }
    });
  };

  const handleUpdateClick = (comment) => {
    console.log("Update icon clicked", comment); // سجل التصحيح
    setCommentForUpdate(comment);
    setUpdateComment(true);
  };

  return (
    <div className="comment-list">
      <h4 className="comment-list-count">2 Comments</h4>
      {[{ id: 1, text: "hallo this is amazing" }, { id: 2, text: "hello, this is great!" }].map(
        (comment) => (
          <div key={comment.id} className="comment-item">
            <div className="comment-item-info">
              <div className="comment-item-username">Fhad Wissam</div>
              <div className="comment-item-time">2 hours ago</div>
            </div>
            <p className="comment-item-text">{comment.text}</p>
            <div className="comment-item-icon-wrapper">
              <i
                onClick={() => handleUpdateClick(comment)}
                className="bi bi-pencil-square"
              ></i>
              <i onClick={deletePostHandler} className="bi bi-trash-fill"></i>
            </div>
          </div>
        )
      )}
      {updateComment && (
        <UpdateCommentModal
          commentForUpdate={commentForUpdate}
          setUpdateComment={setUpdateComment}
        />
      )}
    </div>
  );
};

export default CommentList;
