import React, { useEffect, useState } from 'react';
import PostList from '../../components/posts/PostList';
import { posts } from '../../dummyData';
import './profile.css';
import { toast } from 'react-toastify';

const avatar = process.env.PUBLIC_URL + '/images/user-avatar.png';

const Profile = () => {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!file) {
      toast.warning("Please select a file to upload.");
      return;
    }
    
  };
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  const deleteAccountHandler = () => {
    swal({
      title: "Are you sure?",
      text: "Once deleted, you will not be able to recover profile!",
      icon: "warning",
      buttons: true,
      dangerMode: true,
    }).then((isOk) => {
      if (isOk) {
        dispatch(deleteProfile(user?._id));
        dispatch(logoutUser());
      }
    });
  };

  return (
    <section className='profile'>
      <div className="profile-header">
        <div className="profile-image-wrapper">
          <img 
            src={file ? URL.createObjectURL(file) : avatar} 
            alt="Profile"
            className='profile-image' 
          />
          <form onSubmit={handleSubmit}>
            <abbr title="choose profile photo">
              <label htmlFor="file" className="bi bi-camera-fill upload-profile-photo-icon"></label>
            </abbr>
            <input
              style={{ display: "none" }}
              type="file"
              name="file"
              id="file"
              onChange={handleFileChange}
            />
            <button className="upload-profile-photo-btn" type="submit">
              upload
            </button>
          </form>
        </div>
        <h1 className="profile-username">Fhad Shnde</h1>
        <p className="profile-bio">
          Hello, my name is Fhad, I am a web developer
        </p>
        <div className="user-date-joined">
          <strong>Date Joined : </strong>
          <span>Fri Nov 04 2022</span>
        </div>
        <button className="profile-update-btn">
          <i className="bi bi-file-person-fill"></i>
          Update Profile
        </button>
      </div>
      <div className="profile-posts-list">
        <h2>Fhad Shnde's Posts</h2>
        <PostList posts={posts} />
      </div>
      <button className="delete-account-btn">
        Delete Your Account
      </button>
    </section>
  );
};

export default Profile;
