import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Header from "./components/header/Header.jsx";
import Home from './pages/home/Home.jsx';
import Login from './pages/forms/Login.jsx';
import Register from './pages/forms/Register.jsx';
import PostPage from './pages/post-page/Post-Page.jsx';
import AdminDashboard from './pages/admin/AdminDashboard.jsx'; 
import CreatePage from './pages/create-post/CreatePost.jsx';
import PostDetails from "./pages/post-details/PostDetails.jsx";
import Category from "./pages/category/Category";
import { ToastContainer } from 'react-toastify';
import Profile from './pages/profile/Profile.jsx';
import Footer from './components/footer/Footer.jsx';
import UsersTable from './pages/admin/UsersTable.jsx';
import PostsTable from './pages/admin/PostsTable.jsx';
import CategoriesTable from './pages/admin/CategoriesTable.jsx';
import CommentsTable from './pages/admin/CommentsTable.jsx';
import FrogotPassword from './pages/forms/ForgotPassword.jsx';
import ResetPassword from './pages/forms/ResetPassword.jsx';
import NotFound from './pages/not-found/NotFound.jsx';

function App() {
  return (
    <BrowserRouter>
      <ToastContainer theme='colored' position='top-center' />
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgot-password" element={<FrogotPassword />} />
        <Route path="/reset-password" element={<ResetPassword />} />
        <Route path="/profile/:id" element={<Profile />} />
        <Route path="posts">
          <Route index element={<PostPage />} />
          <Route path="create-post" element={<CreatePage />} />
          <Route path="categories/:category" element={<Category />} />
          <Route path="details/:id" element={<PostDetails />} />
        </Route>
        <Route path="/admin-dashboard" element={<AdminDashboard />}>
        </Route>
        <Route path="/admin-dashboard/users-table" element={<UsersTable />} />
        <Route path="/admin-dashboard/posts-table" element={<PostsTable />} />
        <Route path="/admin-dashboard/categories-table" element={<CategoriesTable />} />
        <Route path="/admin-dashboard/comments-table" element={<CommentsTable />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
}

export default App;
