import { useEffect,useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { posts } from '../../dummyData';
import './post-details.css';
import {toast,ToastContainer} from 'react-toastify'
const PostDetails = () => {
  const { id } = useParams();
  const post = posts.find((post) => post._id === parseInt(id));
  const [file, setFile] = useState(null);
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [id]);

  const updateImageSubmitHandler = (e) => {
    e.preventDefault();
    if(!file) {
      toast.w("Please select a file");
    }
  };

  if (!post) {
    return <div>Post not found</div>;
  }

  return (
    <section className='post-details'>
      <div className="post-details-image-wrapper">
        {post.image ? (
          <img src={post.image} alt={post.title} className="post-details-image"/>
        ) : (
          <p>No image available</p>
        )}
        <form className='update-post-image-form' onSubmit={updateImageSubmitHandler}>
          <label htmlFor="file" className='update-post-label'>
            <i className='bi bi-image-fill'></i>
          </label>
          <input type="file" name='file' id='file'
          style={{ display: 'none' }}
          onChange={(e) => setFile(e.target.files[0])}
          />
          <button type='submit' className='update-post-image-button'>Update</button>
        </form>
      </div>
      <h1 className="post-details-title">{post.title}</h1>
      <div className='post-details-user-info'>
        <img src={post.user.image} alt={post.user.username} className="post-details-user-image" />
        <div className="post-details-user">
          <strong>
            <Link to="/profile/1">
              {post.user.username}
            </Link>
          </strong>
          <span>{post.createdAt}</span>
        </div>
      </div>
      <p className="post-details-description">
        {post.description}
        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aspernatur esse mollitia quae distinctio fugit nesciunt laboriosam molestiae quibusdam? Rerum commodi quisquam deleniti quibusdam quod totam ab voluptatem eligendi impedit alias.
      </p>
      <div className="post-details-icon-wrapper">
        <div>
          <i className='bi bi-hand-thumbs-up'></i>
          <small>{post.likes.length} likes</small>
        </div>
        <div>
          <i className='bi bi-pencil-square'></i>
          <i className='bi bi-trash-fill'></i>
        </div>
      </div>
    </section>
  );
};

export default PostDetails;
