import React from 'react';
import { useParams } from 'react-router-dom';
import { posts } from '../../dummyData';

const PostDetails = () => {
  const { id } = useParams();
  const post = posts.find((post) => post._id === parseInt(id));

  if (!post) {
    return <div>Post not found</div>;
  }

  return (
    <section className='post-details'>
      <div className="post-details-image-wrapper">
          <img src={post.image} alt={post.title} className='post-details-image' />
          <form className='update-post-image-form'>
            <label htmlFor="file" className='update-post-label'>
              <i className='bi bi-image-fill'></i>
            </label>
            <input type="file" name='file' id='file' />
            <button type='submit' className='update-post-image-button'>Update</button>
          </form>
      </div>
      <h1 className="post-dateils-title">{post.title}</h1>
      <div className='post-dateils-user-info'>
        img.
      </div>
    </section>
  );
};

export default PostDetails;
