import './update-profile.css'
import {useState} from 'react'
const user = {
    username: "John Doe",
    bio: "I am a software engineer",
  };

const UpdateProfileModal = ({setUpdateProfile}) => {

  const [username, setUsername] = useState(user.username);
  const [bio, setBio] = useState(user.bio);
  const [password, setPassword] = useState("");

  const formSubmitHandler = (e) => {
    e.preventDefault();
    
    const updatedUser = { username, bio }

    if(password.trim() !== "") {
        updatedUser.password = password;
    }

    setUpdateProfile(false);
}

  return (
    <div className="update-profile">
      <form className="update-profile-form" onSubmit={formSubmitHandler}>
      <abbr title="close">
          <i
            className="bi bi-x-circle-fill update-profile-form-close"
            onClick={() => setUpdateProfile(false)}
          ></i>
        </abbr>
        <h1 className="update-profile-title">Update Your Account</h1>
        <input 
        type="text" 
        value={username}
        placeholder='Username'
        onChange={(e)=>setUsername(e.target.value)}
        className="update-profile-input"  />
        <input
        
        type="text" 
        value={bio}
        onChange={(e)=>setBio(e.target.value)}
        placeholder='Bio'
        className="update-profile-input"  />
        <input 
        type="password" 
        value={password}
        placeholder='Password'
        onChange={(e)=>setPassword(e.target.value)}
        className="update-profile-input"  />
        <button type="submit" className="update-profile-btn">Update Profile</button>
      </form>
    </div>
  )
}

export default UpdateProfileModal
