import './update-post.css'
import {useState} from 'react'
const UpdatePostModal = ({setUpdatePost,post}) => {
  const [title,setTitle] = useState(post.title)
  const [description,setDescription] = useState(post.description)
  const [category,setCategory] = useState(post.category)

  const formSubmitHandler = (e) => {
    e.preventDefault();
    if (title.trim() === "") return toast.error("Post Title is required");
    if (category.trim() === "") return toast.error("Post Category is required");
    if (description.trim() === "")
      return toast.error("Post Description is required");

    setUpdatePost(false);
  };

  return (
    <div className="update-post">
      <form className="update-post-form">
      <abbr title="close">
          <i
            className="bi bi-x-circle-fill update-post-form-close"
            onClick={() => setUpdatePost(false)}
          ></i>
        </abbr>
        <h1 className="update-post-title">Update Post</h1>
        <input 
        type="text" 
        placeholder="Title"
        value={title}
        onChange={(e)=>setTitle(e.target.value)}
        className="update-post-input"  />
        <select 
        classNam="update-post-input"
        value={category}
        onChange={(e)=>setCategory(e.target.value)}
        >
        <option disabled value="">
            Select Category
          </option>
          <option  value="music">music</option>
          <option  value="travelling">travelling</option>
        </select>
        <textarea 
        rows="5" 
        classNam="update-post-textarea"
        value={description}
        onChange={(e)=>setDescription(e.target.value)}
        ></textarea>
        <button type="submit" className="update-post-btn">Update Post</button>
      </form>
    </div>
  )
}

export default UpdatePostModal
