import { useParams, Link } from "react-router-dom";
import "./category.css";
import PostList from "../../components/posts/PostList";
import { useEffect, useState } from "react";
import { posts } from "../../dummyData";

const Category = () => {
  const { category } = useParams();
  const [postsCate, setPostsCate] = useState([]);

  useEffect(() => {
    // محاكاة جلب المنشورات بناءً على الفئة
    const fetchPostsBasedOnCategory = () => {
      const filteredPosts = dummyPosts.filter((post) => post.category === category);
      setPostsCate(filteredPosts);
    };
    fetchPostsBasedOnCategory();
    window.scrollTo(0, 0);
  }, [category]);

  return (
    <section className="category">
      {postsCate.length === 0 ? (
        <>
          <h1 className="category-not-found">
            Posts with <span>{category}</span> category not found
          </h1>
          <Link to="/posts" className="category-not-found-link">
            Go to posts page
          </Link>
        </>
      ) : (
        <>
          <h1 className="category-title">Posts based on {category}</h1>
          <PostList posts={postsCate} />
        </>
      )}
    </section>
  );
};

export default Category;
